import React, {useState, useEffect} from 'react';
import Currency from './CurrencyRow'
import './App.css';
import axios from 'axios';

function App() {
  const [currencies, setCurrencies] = useState([])
  const [fromCurrency, setFromCurrency] = useState('EUR')
  const [toCurrency, setToCurrency] = useState('USD')
  const [fromAmount, setFromAmount] = useState(0)
  const [toAmount, setToAmount] = useState(0)
  const [onChangeAmount, setOnChangeAmount] = useState(true)

  useEffect(() => {
    axios({
      method: 'GET',
      url: 'https://api.exchangeratesapi.io/latest',
      params: {
        base: 'EUR'
      }
    }).then(res => {
      setCurrencies(['EUR', ...Object.keys(res.data.rates)])
    })
  }, [])

  // useEffect(() => {
  //   axios({
  //     method: 'GET',
  //     url: 'https://api.exchangeratesapi.io/latest',
  //     params: {
  //       base: fromCurrency
  //     }
  //   }).then(res => {
  //     setToAmount((fromAmount * res.data.rates[toCurrency]))
  //   })
  // }, [fromAmount, fromCurrency, toCurrency])

  useEffect(() => {
    if (onChangeAmount) {
      updateToAmount()
    }
  }, [fromAmount])

  useEffect(() => {
    if (!onChangeAmount) {
      updateFromAmount()
    }
  }, [toAmount])

  useEffect(() => {
    updateToAmount()
  }, [fromCurrency])

  useEffect(() => {
    updateFromAmount()
  }, [toCurrency])

  function updateFromAmount() {
    axios({
      method: 'GET',
      url: 'https://api.exchangeratesapi.io/latest',
      params: {
        base: toCurrency
      }
    }).then(res => {
      setFromAmount((toAmount * res.data.rates[fromCurrency]))
    })
  }

  function updateToAmount() {
    axios({
      method: 'GET',
      url: 'https://api.exchangeratesapi.io/latest',
      params: {
        base: fromCurrency
      }
    }).then(res => {
      setToAmount((fromAmount * res.data.rates[toCurrency]))
    })
  }

  return (
    <div className="App">

      <header className='title'>Convert</header>

      <Currency
        currencies={currencies}
        rowCurrency={fromCurrency}
        onChangeCurrency={e => setFromCurrency(e.target.value)}
        amount={fromAmount}
        onChangeAmount={e => {
          setOnChangeAmount(true)
          setFromAmount(e.target.value)
        }}
      />

      <div className='equal'>=</div>

      <Currency
        currencies={currencies}
        rowCurrency={toCurrency}
        onChangeCurrency={e => setToCurrency(e.target.value)}
        amount={toAmount}
        onChangeAmount={e => {
          setOnChangeAmount(false)
          setToAmount(e.target.value)
        }}
      />

    </div>
  );
}

export default App;
