import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [fromCurrency, setFromCurrency] = useState('USD')
  const [toCurrency, setToCurrency] = useState('EUR')
  const [fromAmount, setFromAmount] = useState(0)
  const [toAmount, setToAmount] = useState(0)
  const currencies = ['USD', 'EUR', 'MAD']
  const exchangeRate = {
    USD: {
      USD: 1,
      EUR: 1.1,
      MAD: 10.1
    },
    EUR: {
      USD: 0.9,
      EUR: 1,
      MAD: 10
    },
    MAD: {
      USD: 0.09,
      EUR: 0.1,
      MAD: 1
    }
  }

  useEffect(() => {
    axios({
      method: 'GET',
      url: 'https://api.exchangeratesapi.io/latest',
      params: {
        base: fromCurrency
      }
    }).then(res => {
      console.log(res.data.rates.toCurrency)
    })

    setToAmount((fromAmount * exchangeRate[fromCurrency][toCurrency]).toFixed(2))
  }, [fromCurrency])

  useEffect(() => {
    //if (isNaN(toAmount)) setToAmount(0)
    setFromAmount((toAmount * exchangeRate[toCurrency][fromCurrency]).toFixed(2))
  }, [toCurrency])

  function handleFromAmount(e) {
    setFromAmount(e.target.value)
    setToAmount((e.target.value * exchangeRate[fromCurrency][toCurrency]).toFixed(2))
  }

  function handleToAmount(e) {
    setToAmount(e.target.value)
    setFromAmount((e.target.value * exchangeRate[toCurrency][fromCurrency]).toFixed(2))
  }

  function handleFromCurrency(e) {
    setFromCurrency(e.target.value)
  }

  function handleToCurrency(e) {
    setToCurrency(e.target.value)
  }


  return (
    <div className="App">

      <header className='title'>Convert</header>

      <div className='input'>
        <input className='amount' type='number' value={fromAmount} min='0' onChange={handleFromAmount} />
        <select className='currency' value={fromCurrency} onChange={handleFromCurrency}>
          {currencies.map(currency => <option key={currency} value={currency}>{currency}</option>)}
        </select>
      </div>

      <div className='equal'>=</div>

      <div className='input'>
        <input className='amount' type='number' value={toAmount} min='0' onChange={handleToAmount} />
        <select className='currency' value={toCurrency} onChange={handleToCurrency}>
          {currencies.map(currency => <option key={currency} value={currency}>{currency}</option>)}
        </select>
      </div>

    </div>
  );
}

export default App;
