import React, {useState} from 'react';
import './App.css';

function App() {
  const [fromCurrency, setFromCurrency] = useState('USD')
  const [toCurrency, setToCurrency] = useState('EUR')
  const [fromAmount, setFromAmount] = useState(0)
  const [toAmount, setToAmount] = useState(0)
  const currencies = ['USD', 'MAD', 'EUR']
  const exchangeRate = 1.1

  function handleFromAmount(e) {
    setFromAmount(e.target.value)
    setToAmount((e.target.value * exchangeRate).toFixed(2))
  }

  function handleToAmount(e) {
    setToAmount(e.target.value)
    setFromAmount((e.target.value / exchangeRate).toFixed(2))
  }

  function handleFromCurrency(e) {
    console.log('before: ', fromCurrency)
    setFromCurrency(e.target.value)
    console.log('after: ', fromCurrency)
  }

  return (
    <div className="App">

      <div>
        <input type='number' value={fromAmount} min='0' onChange={handleFromAmount} />
        <select className='dropdown' onChange={handleFromCurrency}>
          {currencies.map(currency => <option key={currency}>{currency}</option>)}
        </select>
      </div>

      <div>=</div>

      <div>
        <input type='number' value={toAmount} min='0' onChange={handleToAmount} />
        <div className='dropdown'>
          <button className='dropdownbtn'>{toCurrency}</button>
          <div className='dropdown-content'>
            <a>USD</a>
            <a>EUR</a>
            <a>GBP</a>
          </div>
        </div>
      </div>

    </div>
  );
}

export default App;
