import React from 'react'
import './CurrencyRow.css'

export default function CurrencyRow(props) {
    // const {
    //     fromCurrency,
    //     fromAmount,
    //     changeFromCurrency,
    //     changeFromAmount
    // } = props
    const {
        currencies,
        rowCurrency,
        onChangeCurrency
    } = props

    function handleCurrency(e) {
        onChangeCurrency(e.target.value)
    }

    return (
        <div className='CurrencyRow'>
            <input className='amount' type='number' defaultValue='1' min='0' />
            <select className='dropdown' value={rowCurrency} onChange={handleCurrency} >
                {currencies.map(currency => <option key={currency}>{currency}</option>)}
            </select>
        </div>
    )
}
